                    ynadvsearch.eu = function(event){
                        var item_type='';
                        var maxre = 10;
                        $$(".tag-autosearch").each(function(e){
                            var doc = document.body;
                            doc.removeChild(e);
                        });

                        new YnAdsAutocompleter.RequestAdSearch.JSON('global_search_field', '/application/modules/Core/externals/scripts/ynadvsearch.php', {
                            'postVar': 'search',
                            'className': 'tag-autosearch',
                            'cache': false,
                            'tokenFormat' : 'object',
                            'tokenValueKey' : 'label',
                            'maxChoices': 20,
