                                {	l++; }
                                trimquery = trimquery.substring(l);
                                if (trimquery && ynadvsearch.is_space == false)
                                {   //application/modules/Core/externals/images/ajax-loader.gif
                                    $('global_search_field').setStyle('background', "url('/public/images/search.png') no-repeat right ");
                                }
                            },
                            onComplete: function(){
                                $('global_search_field').setStyle('background', '');
                                ;
                                item_type = '';
                            }
                        });
                    };
                    window.addEvent('domready', ynadvsearch.init);
