                    jQuery("#feed_loading").hide();
                    jQuery("#light").hide();
                    jQuery("#fade").hide();

                    jQuery("body").css('height', "auto");
                    jQuery(".user_main_thumb").attr('src', newImage);
                    jQuery(".owner_photo_icon a img").attr('src', newImage);
                    jQuery(".comment_owner_photo_icon a img").attr('src', newImage);
                    jQuery(".feed_item_photo_comment a img").attr('src', newImage);
                    jQuery(".user_profille_dp img").attr('src', newImage);
                    jQuery("#brand_photo img").attr('src', newImage);
                }
            });// ajax for sending image data to action

        });// click .export
    });
</script>
<script>
    jQuery(".comment-box").attr("onkeypress", "commentOnEnter(event, this);");
    function commentOnEnter(e, thisInput){
        if(window.event){ // IE					
            key = e.keyCode;
            //alert(key);
        }else
        if(e.which){ // Netscape/Firefox/Opera		
            //alert(key);

            key = e.which;
        }
        if(key == 13){
            var idCommentBox = thisInput.id;
            //alert(idCommentBox);
            var commentBody = jQuery(idCommentBox).val();
            if(commentBody != ""){
                var actionId = idCommentBox.replace ( /[^\d.]/g, '' );
                //alert(" action id "+actionId);
                jQuery("#activity-comment-submit-" + actionId).click();
                //alert("#activity-comment-submit-" + actionId);
            }
        }
    }

    window.setInterval(function(){
        if(jQuery("#im_main_notification").hasClass("im_main_settings_online") ){
            jQuery(".im_item_tooltip_settings").html("Go Offline");
        }

        if(jQuery("#im_main_notification").hasClass("im_main_settings_offline") ){
            jQuery(".im_item_tooltip_settings").html("Go Online");
        }

    }, 1000);

</script>
